import clipboardy from 'clipboardy';

// PLOP_CODE_START
export const SetCopiedText = (text) => {
  /* PLOP_INJECT_CODE */

  try {
    clipboardy.writeSync(text);
    return 42;
  } catch (err) {
    console.log(err);
    return -1;
  }
};
